import React from 'react'

import Link from 'next/link'

export const Post = ({ lang, title, path, date, description }) => {
	const options = {
		weekday: 'long',
		year: 'numeric',
		month: 'long',
		day: 'numeric'
	}
	const languageClass = lang === 'ar' ? 'u-rightToLeft' : null
	const localeDate = new Date(JSON.parse(date)).toLocaleDateString(
		lang === 'ar' ? 'ar-EG' : 'en-US',
		options
	)

	return (
		<li className={languageClass}>
			<Link href={`/blog/${path}`}>
				<a>
					<h1>{title}</h1>
				</a>
			</Link>
			<p>{description}</p>
			<span>{localeDate}</span>
		</li>
	)
}
