import React from 'react'
import ReactMarkdown from 'react-markdown/with-html'
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter'
import { a11yDark } from 'react-syntax-highlighter/dist/cjs/styles/hljs'

import SEO from '../../components/SEO'

import { getAllSlugs, getPostBySlug } from '../../api'
import { formatDate } from '../../utils'

const renderers = {
	code: ({ language, value }) => {
		return (
			<SyntaxHighlighter
				style={a11yDark}
				language={language}
				children={value}
				showLineNumbers
				children={value}
			/>
		)
	},
	pre: ({ language, value }) => {
		return (
			<SyntaxHighlighter
				style={a11yDark}
				language={language}
				children={value}
				showLineNumbers
				children={value}
			/>
		)
	}
}

export default function PostTemplate({ html, frontmatter }) {
	const { lang, date, tags, title, path } = frontmatter
	// const languageClass = lang === 'ar' ? 'rtl' : null

	return (
		<>
			<SEO {...frontmatter} />
			<div>
				<span>{date}</span>
				<span> â€” </span>
				<span>
					<a
						href={`https://twitter.com/intent/tweet?text=${title}&url=https://iamnabil.netlify.app${path}&hashtags=${tags}`}
					>
						Tweet This
					</a>
				</span>
				<h1 className='mt-0 mb-4'>{title}</h1>
			</div>

			<article>
				<ReactMarkdown renderers={renderers} allowDangerousHtml>
					{html}
				</ReactMarkdown>
			</article>
		</>
	)
}

export async function getStaticProps({ params: { slug } }) {
	const post = getPostBySlug(slug)

	post.frontmatter.date = formatDate(
		post.frontmatter.date,
		post.frontmatter.lang
	)

	return {
		props: {
			...post
		}
	}
}

export async function getStaticPaths() {
	const paths = getAllSlugs().map(slug => ({
		params: { slug: slug.replace('.md', '') }
	}))

	return {
		paths,
		fallback: false
	}
}
