import fs from 'fs'
import matter from 'gray-matter'
import React from 'react'

function PostTemplate(props) {
	return <div>Here we'll load "{props.slug}"</div>
}

export async function getStaticProps({ params: { slug } }) {
	const file = fs.readFileSync(`blog/${slug}`, 'utf-8')
	const parsed = matter(file)

	console.log(parsed.data.date, typeof parsed.data.date)

	return {
		props: {
			html: parsed.content,
			frontmatter: {
				...parsed.data,
				date: parsed.data.date.toJSON()
			},
			slug: parsed.data.path
		}
	}
}

export async function getStaticPaths() {
	const paths = fs
		.readdirSync('blog')
		.filter(path => path.endsWith('.md'))
		.map(path => ({ params: { slug: path } }))

	return {
		paths,
		fallback: false
	}
}

export default PostTemplate
